select person.*, kontakt.*, seminar.datum_begin, seminar.kursnr from person, kontakt, buchung, seminar where seminar_id = seminar.id AND seminar.seminar_art_id="DI-G" AND person.id = buchung.person_id AND person.kontakt_id = kontakt.id AND person_id not in (select person_id from buchung, seminar where seminar_id = seminar.id and seminar.seminar_art_id = 'DI-AK') GROUP BY buchung.person_id ORDER BY kontakt.firma